<?phpinclude "config.php";$cart = $_POST['cart'];$method = $_POST['method'];//$cart = "EveningSession6-April7,14,21,and28-MiddleSchoolStudySkills:1:0.05:0:E06MSSS:84";//$method = "monkey";$cart = str_replace(' ', '', $cart);echo $cart . "<br />";//Separate each ordered item$products = explode("~", $cart);$i = 0;//Separate each part of the orderforeach ($products as $product1){	$items = explode(":", $product1);	$modelnums[$i] = $items[4];	$quantities[$i] = $items[1];		$regid[$i] = $items[5];//registration ID		//Mark as paid - structure A	//$query2 ="UPDATE content_field_status SET field_status_value = 'paid' WHERE nid = ". $regid[$i];	//Structure B	if ($method != "Check or Money Order"){		$query2 ="UPDATE content_type_register SET field_status_value = 'paid' WHERE nid = ". $regid[$i];		db_query($query2);	}		//Select the model and stock value	$sql =  "SELECT c.field_model_value, c.field_stock_value, c.nid FROM content_type_class c LEFT JOIN content_type_register r ON r.field_section_value = c.nid WHERE r.nid =". $regid[$i];	$result = db_query($sql);	$order = mysql_fetch_array($result);		//Reduce stock value	$updatedqty = $order['field_stock_value'] - $quantities[$i];		$sql2 = "UPDATE content_type_class SET field_stock_value = ". $updatedqty." WHERE nid = ". $order['nid'];	db_query($sql2);		$i++;}//end foreach?>Yay!